package com.dzavorin.solutions.rollinghash;

import java.util.HashSet;
import java.util.Set;

public class LongestDuplicateSubstring {

    public String longestDupSubstring(String s) {
        String res = "";
        int lo = 1;
        int hi = s.length() - 1;
        while (lo <= hi) {
            int m = lo + (hi - lo) / 2;
            String r = rabinKarp(m, s);
            if (r != null) {
                res = r;
                lo = m + 1;
            } else {
                hi = m - 1;
            }
        }
        return res;
    }

    public String rabinKarp(int m, String s) {
        String sub = s.substring(0, m);
        long hash = hash(sub);

        Set<Long> set = new HashSet<>();
        set.add(hash);

        long pow = Math.round(Math.pow(31, m - 1));

        for (int i = m; i < s.length(); i++) {
            hash = nextHash(hash, pow, s.charAt(i - m), s.charAt(i));
            if (!set.add(hash)) {
                return s.substring(i - m + 1, i + 1);
            }
        }
        return null;
    }

    private long hash(String s) {
        long res = 0;
        long a = 1;

        int n = s.length();
        for (int k = n; k >= 1; k--) {
            char ch = s.charAt(k - 1);
            res += (ch) * a;
            a = (a * 31);
        }
        return res;
    }

    private long nextHash(long hash, long pow, char left, char right) {
        return (hash - (left) * pow) * 31 + right;
    }

    public static void main(String[] args) {
        System.out.println(new LongestDuplicateSubstring().longestDupSubstring(""));
        System.out.println(new LongestDuplicateSubstring().longestDupSubstring("banana"));
        System.out.println(new LongestDuplicateSubstring().rabinKarp(4, ""));
    }

}
